interface BaseFileResult {
  source: string;
  fullpath: string;
  path: string;
  alias: string;
}

declare function create(path: string): Promise<({
    status: "SUCCESS";
} & BaseFileResult & {
    db_name: string;
    name: string;
}) | ({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
})>;
declare function open(path: string): Promise<({
    status: "SUCCESS";
} & BaseFileResult & {
    db_name: string;
    name: string;
}) | ({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
})>;
declare function close(path: string): Promise<({
    status: "SUCCESS";
} & BaseFileResult & {
    db_name: string;
    name: string;
}) | ({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
})>;
declare function remove(path: string): Promise<({
    status: "SUCCESS";
} & BaseFileResult & {
    db_name: string;
    name: string;
}) | ({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
})>;
declare function execute(options: {
    path: string;
    sql: string;
    async?: boolean;
    multiple?: boolean;
}): Promise<({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
}) | ({
    status: "SUCCESS";
} & BaseFileResult & {
    db_name: string;
    name: string;
    column: string[];
    column_count: number;
    columns: string[];
    fields: string[];
    row_list: unknown[];
    rows: unknown[];
})>;

export { close, create, execute, open, remove };
