type NativeResult<S = {}, F = {}> =
  | ({ status: "SUCCESS" } & S)
  | ({ status: "FAIL" } & F);

interface BaseFileResult {
  source: string;
  fullpath: string;
  path: string;
  alias: string;
}

declare function create(options: {
    path: string;
    type: "FILE" | "DIR";
}): NativeResult<BaseFileResult, {
    error?: string;
    message?: string;
}>;
declare function read(options: {
    path: string;
    encoding?: "UTF-8" | "EUC-KR" | "BASE64";
    indicator?: boolean;
}): Promise<({
    status: "SUCCESS";
} & BaseFileResult & {
    data: string;
    size: number;
    encode: string;
}) | ({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
})>;
declare function remove(path: string): NativeResult<BaseFileResult, {
    error?: string;
    message?: string;
}>;
declare function info(path: string): NativeResult<BaseFileResult & {
    type: "FILE" | "DIR" | "UNKNOWN";
    name: string;
    list?: (BaseFileResult & {
        name: string;
    })[];
    size?: string;
    created: string;
    updated: string;
    create: string;
    update: string;
}, {
    error?: string;
    message?: string;
}>;
declare function list(path: string, filter?: "ALL" | "FILE" | "DIR"): NativeResult<{
    dir_count: number;
    dir_list: (BaseFileResult & {
        name: string;
    })[];
    list: (BaseFileResult & {
        name: string;
    })[];
}, {
    error?: string;
    message?: string;
}>;
declare function copy(options: {
    source: string;
    destination: string;
    overwrite?: boolean;
    type?: "FILE" | "DIR";
    permission?: "NORMAL" | "RESOURCE";
    indicator?: boolean;
    progress?: (result: {
        total: string;
        current: string;
        index: string | number;
        count: number;
        srcPath: string;
        dstPath: string;
    }) => void;
}): Promise<({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
}) | ({
    status: "SUCCESS";
} & BaseFileResult & {
    type: string;
})>;
declare function move(options: {
    source: string;
    destination: string;
    overwrite?: boolean;
    type?: "FILE" | "DIR";
    permission?: "NORMAL" | "RESOURCE";
    indicator?: boolean;
    progress?: (result: {
        total: string;
        current: string;
        index: string | number;
        count: number;
        srcPath: string;
        dstPath: string;
    }) => void;
}): Promise<({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
}) | ({
    status: "SUCCESS";
} & BaseFileResult & {
    type: string;
})>;
declare function write(options: {
    path: string;
    contents: string;
    encoding?: "UTF-8" | "EUC-KR" | "BASE64";
    indicator?: boolean;
}): Promise<{
    status: "SUCCESS";
} | ({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
})>;
declare function picker(options: {
    iosType?: "data" | "item" | "content" | "text" | "image" | "video" | string;
    type?: "*/*" | string;
}): Promise<BaseFileResult[] | {
    status: "FAIL";
    error?: string;
    message?: string;
}>;

export { copy, create, info, list, move, picker, read, remove, write };
