type NativeResult<S = {}, F = {}> =
  | ({ status: "SUCCESS" } & S)
  | ({ status: "FAIL" } & F);

interface BaseFileResult {
  source: string;
  fullpath: string;
  path: string;
  alias: string;
}

declare function camera(options?: {
    path?: string;
    filename?: string;
    mediaType?: "PHOTO" | "VIDEO";
    direction?: "BACK" | "FRONT";
    allowEdit?: boolean;
    saveAlbum?: boolean;
    overwrite?: boolean;
}): Promise<({
    status: "SUCCESS";
} & BaseFileResult & {
    filesize: string;
    filename: string;
    created: string;
    updated: string;
    name: string;
    size: string;
    saveDate: string;
}) | ({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
}) | {
    status: "CANCEL";
    error?: string;
    message?: string;
}>;
declare function album(path: string): Promise<({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
}) | ({
    status: "SUCCESS";
} & BaseFileResult)>;
declare function picker(options?: {
    path?: string;
    mode?: "SINGLE" | "MULTI";
    mediaType?: "PHOTO" | "VIDEO" | "MOVIE" | "AUDIO" | "ALL";
    allowEdit?: boolean;
    column?: number;
    detail?: boolean;
    zoom?: boolean;
    maxCount?: number;
}): Promise<({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
}) | ({
    status: "SUCCESS";
} & BaseFileResult & {
    name: string;
    size: string;
    duration: number;
    type?: string;
    created: string;
    updated: string;
    orientation: number;
    saveDate: string;
    startDate: string;
    endDate: string;
}) | {
    status: "CANCEL";
    error?: string;
    message?: string;
}>;
declare function removeLibrary(options: {
    removeFiles: string | string[];
    mediaType?: "ALL" | "PHOTO" | "VIDEO" | "AUDIO";
}): Promise<{
    status: "SUCCESS";
} | ({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
})>;
declare function play(options: {
    path: string;
    playType?: "NATIVE" | "WEB" | "YOUTUBE";
}): void;
declare function record(options: {
    path: string;
    filename?: string;
    overwrite?: boolean;
}): Promise<({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
}) | ({
    status: "SUCCESS";
} & BaseFileResult & {
    filesize: string;
    filename: string;
    created: string;
    updated: string;
    name: string;
    size: string;
    fullpath: string;
    saveDate: string;
})>;
declare function getMediaByPath(path: string): NativeResult<BaseFileResult & {
    created: string;
    duration: string;
    filename: string;
    filesize: string;
    format: string;
    name: string;
    orientation: number;
    pixelHeight: string;
    pixelWidth: string;
    saveDate: string;
    size: string;
    type: "PHOTO" | "VIDEO" | "UNKNOWN";
    updated: string;
}, {}>;
declare function optimize(options: {
    source: string;
    destination: string;
    overwrite?: boolean;
    dimesion?: {
        width?: string;
        height?: string;
    };
    quality?: number;
    format?: "PNG" | "JPG";
}): Promise<({
    status: "SUCCESS";
} & BaseFileResult) | {
    status: "FAIL";
    error?: string;
    message?: string;
}>;
declare function crop(options: {
    source: string;
    destination: string;
    title?: string;
    doneButtonTitle?: string;
    cancelButtonTitle?: string;
    overwrite?: boolean;
    dimesion?: {
        width?: string;
        height?: string;
    };
    quality?: number;
    format?: "PNG" | "JPG";
}): Promise<({
    status: "SUCCESS";
} & BaseFileResult) | {
    status: "FAIL";
    error?: string;
    message?: string;
}>;

export { album, camera, crop, getMediaByPath, optimize, picker, play, record, removeLibrary };
