function uploadHttp(options) {
    return new Promise((resolve) => {
        M.net.http.upload({
            ...options,
            progress: (total, current, remaining, percentage) => {
                if (typeof options.progress === "function") {
                    options.progress(total, current, remaining, percentage);
                }
            },
            finish: (statusCode, header, body) => {
                resolve({ code: statusCode, header, body });
            },
        });
    });
}
function donwloadHttp(options) {
    return new Promise((resolve) => {
        M.net.http.download({
            ...options,
            progress: (...args) => {
                if (typeof options.progress === "function") {
                    options.progress(...args);
                }
            },
            finish: (statusCode, header, fileInfo, status, error) => {
                resolve({ statusCode, header, file: fileInfo, status, error });
            },
        });
    });
}

export { donwloadHttp, uploadHttp };
