declare function confirm(options: {
    title?: string;
    message: string;
    buttons?: string[];
}): Promise<number>;
declare function datePicker(options: {
    dateType?: string;
    initDate?: string;
    minDate?: string;
    maxDate?: string;
    interval?: string;
    mode?: string;
}): Promise<({
    status: "SUCCESS";
} & Record<string, any>) | ({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
})>;
declare function toast(message: string, time?: "SHORT" | "LONG" | number): void;
declare function listPicker<T = any>(options: {
    mode?: "SINGLE" | "MULTI";
    title?: string;
    message?: string;
    cancelable?: boolean;
    buttons?: string[];
    list: {
        title: string;
        value: string;
    }[];
    selected?: number | number[];
}): Promise<{
    buttonIndex: number;
    selected: T;
}>;

export { confirm, datePicker, listPicker, toast };
