function confirm(options) {
    return new Promise((resolve) => {
        M.pop.alert({
            ...options,
            callback: (index) => {
                resolve(index);
            },
        });
    });
}
function datePicker(options) {
    return new Promise((resolve) => {
        M.pop.date({
            ...options,
            callback: (status, result) => {
                resolve({ ...result, status });
            },
        });
    });
}
function toast(message, time) {
    M.pop.instance(message, time);
}
function listPicker(options) {
    return new Promise((resolve) => {
        M.pop.list({
            ...options,
            callback: (index, info) => {
                resolve({ buttonIndex: index, selected: info });
            },
        });
    });
}

export { confirm, datePicker, listPicker, toast };
