interface BaseFileResult {
  source: string;
  fullpath: string;
  path: string;
  alias: string;
}

declare function zip(options: {
    path: string;
    list?: {
        path: string;
        name: string;
    }[];
    overwrite?: boolean;
    indicator?: boolean;
    password?: string;
}): Promise<({
    status: "SUCCESS";
} & BaseFileResult & {
    size: number;
}) | ({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
})>;
declare function unzip(options: {
    path: string;
    destination: string;
    overwrite?: boolean;
    indicator?: boolean;
    password?: string;
}): Promise<({
    status: "FAIL";
} & {
    error?: string;
    message?: string;
}) | ({
    status: "SUCCESS";
} & BaseFileResult & {
    size: number;
})>;

export { unzip, zip };
