type PAGE_ORIENTATION =
  | "DEFAULT"
  | "PORTRAIT"
  | "PORT"
  | "LANDSCAPE"
  | "LAND"
  | "PORTREV"
  | "LANDREV"
  | "ALL";

declare function scan(options: {
    flash?: boolean;
    fadeToggle?: boolean;
    orientation?: PAGE_ORIENTATION;
    page?: string;
    captureScale?: {
        width: number;
        height: number;
    };
    filterType?: "NONE" | "PARTIAL" | "EAN8" | "UPCE" | "ISBN10" | "UPCA" | "EAN13" | "ISBN13" | "I25" | "DATABAR" | "DATABAR_EXP" | "CODABAR" | "CODE39" | "PDF417" | "QRCODE" | "CODE93" | "CODE128" | (string & {});
    useGuideLine?: boolean;
    libType?: "mlkit" | "Zbar" | "ZXing" | "iOS";
}): Promise<{
    status: "SUCCESS";
    format: string;
    text: string;
} | {
    status: "FAIL";
    error?: string;
    message?: string;
}>;

export { scan };
